<?phpif (!class_exists('UpiCRMExportImport')) {    class UpiCRMExportImport {        var $wpdb;        public function __construct() {            global $wpdb;            $this->wpdb = &$wpdb;        }                function export_csv_from_get_request() {                        //Default: 0            $only_latest = false;            if (isset($_GET['only_latest']) && $_GET['only_latest'] == 1) {                $only_latest = true;            }                        //Default: 0            if (isset($_GET['show_all_fields']) && $_GET['show_all_fields'] == 1) {                $export_fields = false;            } else {                $show_fields = get_option('upicrm_export_show_fields',1);                if (isset($show_fields) && count($show_fields) > 0) {                    foreach ($show_fields as $export) {                        $export_fields['content'][$export] = $export;                    }                }            }                        //Default: 1            $fields_order = 2;            if (isset($_GET['custom_order']) && $_GET['custom_order'] == 0) {                $fields_order = 1;            }                                        upicrm_export_csv($only_latest,false,$export_fields,$fields_order);        }    }   }